sp_widget.client_script:
api.controller=function($rootScope) {
  /* widget controller */
  var c = this;
	c.selectItem = function(index){
		var id = c.data.notes[index].sys_id;
		console.log('Note ID: '+ id);
		$rootScope.noteID = id;
		$rootScope.$emit('temp',id);
	}
};

sp_widget.script:
(function() {
  /* populate the 'data' object */
  /* e.g., data.table = $sp.getValue('table'); */


	data.fields = "number,title,sys_id"
	data.notes=[];
	var noteGR = new GlideRecord('x_snc_createnotes_note');
	noteGR.addQuery('user',gs.getUser().getID());
	noteGR.orderByDesc('sys_created_on');
	noteGR.query();
	while(noteGR.next()){
		var noteObj = {};
		$sp.getRecordDisplayValues(noteObj,noteGR,data.fields);
		noteObj.note = noteGR.getValue('note').slice(0,20);
		data.notes.push(noteObj);


	}




})();
