sp_widget.client_script:
api.controller=function($rootScope) {
  /* widget controller */
  var c = this;
	$rootScope.$on('temp',function(event,data){

		console.log('Listener caught ID: '+ $rootScope.noteID);

		//calls server script and passes custom input
		c.server.get({
			action: 'getNote',
			noteID: $rootScope.noteID

			//when promise is returned fromm server 'then' logic executes
		}).then(function(r){
			c.data.title = r.data.note.title;
			c.data.note = r.data.note.note;
			c.data.noteID = r.data.note.sys_id;
		})

		c.updateNote = function(){
			c.server.get({
				action: 'updateNote',
				noteID: c.data.noteID,
				noteBody: c.data.note,
				noteTitle: c.data.title
			}).then(function(r){

			});
		}




	})



};

sp_widget.script:
(function() {
  /* populate the 'data' object */
  /* e.g., data.table = $sp.getValue('table'); */

	if(input && input.noteID){
		var noteGRr = new GlideRecord('x_snc_createnotes_note');
		if(noteGRr.get(input.noteID)){
			if(input.action == 'getNote'){
				data.note = {};
				$sp.getRecordValues(data.note,noteGRr,"title,note,sys_id");
			}
			else if (input.action == 'updateNote'){
				noteGRr.title= input.noteTitle;
				noteGRr.note= input.noteBody;
				noteGRr.update()
			}
		}
	}




})();
